        icon.put("name", type.getVal());        icon.put("theme", "outline");        // icon.put("icon", i.outlineType());        if (i.setRes(icon, false)) {        }        iconsDefs.add(icon);      }      iconsDefsWithCheckSum = new IconsDefs(iconsDefs);    }    String verToken = this.getString("vertoken");    this.setBizResult(context, iconsDefsWithCheckSum.getIcons(verToken));  }  private static class IconsDefs {    final JSONArray iconsDefs;    final String checkToken;    public IconsDefs(JSONArray iconsDefs) {      this.iconsDefs = iconsDefs;      this.checkToken = DigestUtils.md5Hex(JsonUtil.toString(iconsDefs));    }    public JSONObject getIcons(String verToken) {      JSONObject result = new JSONObject();      String oldCheckToken = this.checkToken;      result.put("verToken", oldCheckToken);      result.put("iconsDefs", oldCheckToken.equals(verToken) ? new JSONArray() : iconsDefs);      return result;    }    //    public void add(JSONObject icon) {    //      iconsDefs.add(icon);    //    }  }}